import {
	CheckOutlined,
	LikeOutlined,
	StopOutlined,
	ThunderboltOutlined,
} from '@ant-design/icons';
import { Avatar, Statistic, Table } from 'antd';
import type { ColumnsType } from 'antd/es/table';
import millify from 'millify';
import { useParams } from 'react-router-dom';
import noImage from '../assets/images/icons/no-image.jpeg';
import Helmet from '../components/Helmet';
import AsyncWrapper from '../layouts/AsyncWrapper';
import { useGetCoinMarketsQuery } from '../redux/features/coins.feature';

const columns: ColumnsType<MarketCurrency> = [
	{
		title: 'Rank',
		dataIndex: 'rank',
		key: 'uuid',
		align: 'center',
	},
	{
		title: 'Avatar',
		dataIndex: 'iconUrl',
		key: 'exchange.uuid',
		align: 'center',
		render: (_, record) => (
			<Avatar
				src={record.exchange.iconUrl || noImage}
				alt={record.exchange.name}
				className='rounded-none'
			/>
		),
	},
	{
		title: 'Name',
		dataIndex: 'exchange.name',
		key: 'uuid',
		render: (_, record) => record.exchange.name,
	},
	{
		title: 'Market Share',
		dataIndex: 'marketShare',
		key: 'uuid',
		align: 'center',
	},
	{
		title: 'BTC Price',
		dataIndex: 'btcPrice',
		key: 'uuid',
		align: 'center',
		render: (_, record) => millify(parseFloat(record.btcPrice)),
	},
	{
		title: 'Recommended',
		dataIndex: 'recommended',
		key: 'uuid',
		align: 'center',
		render: (_, record) =>
			record.recommended ? (
				<CheckOutlined className='text-green-600' />
			) : (
				<StopOutlined className='text-red-600' />
			),
	},
	{
		title: 'Price',
		dataIndex: 'price',
		key: 'uuid',
		align: 'center',
		render: (_, record) => millify(parseFloat(record.price)),
	},
	{
		title: '24h Volume',
		dataIndex: '24hVolume',
		key: 'uuid',
		align: 'center',
		render: (_, record) => millify(parseFloat(record['24hVolume'])),
	},
];

function Market() {
	const params = useParams();

	const { isFetching, error, data } = useGetCoinMarketsQuery({
		coinId: params?.coinId,
	});

	return (
		<AsyncWrapper loading={isFetching} error={error} fulfilled={Boolean(data)}>
			<Helmet
				title='Market'
				description='Find markets on different exchanges that trade a specific coin.
				This endpoint requires the ultra plan or higher.'
			/>
			{Boolean(data) && (
				<>
					<h1 className='mb-5 mt-10 text-xl'>Market Crypto Stats</h1>
					<div className='mb-20 md:flex'>
						<Statistic
							title='24h Volume'
							prefix={<ThunderboltOutlined />}
							value={millify(data.data.stats['24hVolume'])}
						/>
						<Statistic
							title='Total'
							prefix={<LikeOutlined />}
							value={millify(data.data.stats['total'])}
							className='ml-40'
						/>
					</div>

					<Table
						scroll={{ x: 'max-content' }}
						columns={columns}
						dataSource={data.data.markets}
						pagination={{ pageSize: 20 }}
					/>
				</>
			)}
		</AsyncWrapper>
	);
}

export default Market;
